const LocationLevel = () => {
  const [walk, setWalk] = useState(false);
  const [bike, setBike] = useState(false);
  const [car, setCar] = useState(false);
  const [loading, setLoading] = useState(false);
  const nickName = useRecoilValue(loginNickNameState);
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (walk || bike || car) {
      setLoading(true);
      setTimeout(() => {
        navigate('/sharingrequest');
      }, 1000);
    }
  };

  const Loading = () => {
    return (
      <LoadingWrapper loading={loading} className="overlay">
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100%',
          }}
        >
          <img width={300} src="loading.gif" alt="loading.gif" />
        </div>
      </LoadingWrapper>
    );
  };

  return (
    <Wrapper>
      <Loading />
      <Back />
      <Location>
        현위치 제주도 첨단로 242
        <ChevronDownIcon />
      </Location>
      <SearchText>
        <SearchTextTop>
          <span style={{ fontWeight: 'bold' }}>{nickName}</span>님을 위한
        </SearchTextTop>
        주변 귤 농가를 찾아볼게요
      </SearchText>
      <ChoiceText>이동수단을 선택해주세요</ChoiceText>
      <ButtonWrapper>
        <button
          onClick={() => {
            setWalk(!walk);
          }}
        >
          <Walk id={walk ? '#F57D14' : '#EFEFF0'} />
        </button>
        <button
          onClick={() => {
            setBike(!bike);
          }}
        >
          <Bike id={bike ? '#F57D14' : '#EFEFF0'} />
        </button>
        <button
          onClick={() => {
            setCar(!car);
          }}
        >
          <Car id={car ? '#F57D14' : '#EFEFF0'} />
        </button>
      </ButtonWrapper>
      <button
        onClick={() => handleSubmit()}
        style={{ position: 'absolute', bottom: '60px', right: '42px' }}
      >
        <svg
          width="56"
          height="56"
          viewBox="0 0 56 56"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <rect width="56" height="56" rx="8" fill="#F57D14" />
          <path
            d="M15 27.4118L24.1765 38L41 18"
            stroke="white"
            strokeWidth="1.5"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>
    </Wrapper>
  );
};

export default LocationLevel;

